import { createSlice } from '@reduxjs/toolkit'

import { newKey } from '../../utils'

// =============== Slice Object ===============
export const linkItemListSlice = createSlice({
    name:'navBarList',
    initialState:[],
    reducers:{
        addNavItem:(state, action) => {
            const key = newKey()            
            console.log('key: '+key)
            console.log('payload: '+action.payload)

            state.push({
                navItemKey:key,
                title:action.payload,
                isSelected:false,
                linkItemList:[]
            })
        },
        deleteNavItem:(state, action) => {
            return state.filter(navItem => navItem !== action.payload)
        },
        toggleIsSelected:(state, action) => {

        },

        addLinkItem:(state, action) => {                       
            return state.filter(navItem => {
                if(navItem.isSelected){
                    const key = newKey()
                    navItem.linkItemList.push({
                        itemKey:key,
                        url:action.payload,
                        isFav:false
                    })
                }
                console.log('link item was not added as no nav item is selected')
                return state
            })

            // state.push({            
            //     // navItemKey
