        },
        updateLinkItemUrl:(state, action) => {

        },
        deleteLinkItem:(state, action) => {
            //return state.
        },

        toggleFav:(state, action) => {

        }
    }
})

// =============== Selectors ===============
export const selectNavBarList = (state) => linkItemListSlice

// =============== Exports ===============
export const {
    addNavItem,
    deleteNavItem,
    addLinkItem,
    updateLinkItemUrl,
    deleteLinkItem,
    toggleFav
} = linkItemListSlice.actions

export default linkItemListSlice.reducer
