import { createSlice } from '@reduxjs/toolkit'

import { newKey } from '../../utils'

// =============== Slice Object ===============
export const navBarSlice = createSlice({
    name:'navBarList',
    initialState:[],
    reducers:{
        addNavItem:(state, action) => {         
            let isSelectedState = false
            if(state.length === 0)
                isSelectedState = true

            state.push({
                navItemKey:newKey(),
                title:action.payload,
                isSelected:isSelectedState,
                linkItemList:[]    
            })
        },        
        // navItemKey
        // title
        // isSelected
        // linkItemList

        deleteNavItem:(state, action) => {
            if(state.length === 1)
                return []


            // index of navItem to delete
            let removeIndex
            for(let i=0; i<state.length; ++i){
                if(action.payload === state[i].navItemKey){
                    removeIndex = i
                    return [
                        ...state.slice(0, removeIndex),
                        ...state.slice(removeIndex+1, state.length)
                    ]   
                }
            }                      
        },

        toggleIsSelected:(state, action) => {
            // first de-select the current selected nav item
            for(let i=0; i<state.length; ++i){
                if(state[i].isSelected)
                    state[i].isSelected = false
            }
            // second, find the nav item which needs to be selected
            for(let i=0; i<state.length; ++i)
                if(state[i].navItemKey === action.payload)
                    state[i].isSelected = true

            return state
        },

        addLinkItem:(state, action) => {            
            for(let i=0; i<state.length; ++i){
                if(state[i].isSelected){                    
                    state[i].linkItemList.push({
                        linkItemKey:newKey(),
                        url:action.payload,
                        isFav:false,
                        isInFocus: false
                    })                    
                }                
            }            
                // navItemKey
                // title (nav bar display text)  
                // sub list of urls
                    // linkItemKey
                    // url
                    // isFav

        },
        updateLinkItemUrl:(state, action) => {

        },
        deleteLinkItem:(state, action) => {
            //return state.
        },

        toggleFav:(state, action) => {

        },

        toggleFocus:(state, action) => {
            let navItemIndex
            let linkItemIndex
            for(let i=0; i<state.length; ++i){
                if(state[i].isSelected){
                    navItemIndex = i
                    for(let j=0; j<state[i].linkItemList.length; ++j){
                        if(state[i].linkItemList[j] === action.payload){
                            linkItemIndex = j
                            break
                        }
                    }
                }
            }
            let newState = state
            newState[navItemIndex].linkItemList[linkItemIndex].isInFocus ? flase : true
            return newState
            
            // return [
            //     ...state.slice(0, navItemIndex),
            //     {
            //         navItemKey: state[navItemIndex].navItemKey,
            //         title: state[navItemIndex].title,
            //         isSelected: state[navItemIndex].isSelected,
            //         linkItemList:

            //     state[navItemIndex].linkItemList[linkItemIndex]
            //     },                
            //     ...state.slice(navItemIndex+1, state.length)
            // ]
        }
    }
})

// =============== Selectors ===============
export const selectNavBarList = state => state.navBarList

export const selectIsSelectedLinkItemList = state => {
    for(let i=0; i<state.navBarList.length; ++i){
        if(state.navBarList[i].isSelected)
            return state.navBarList[i].linkItemList
    }
}

export const selectFocusedUrlText = state => {

}

//export const select


// =============== Exports ===============
export const {
    addNavItem,
    deleteNavItem,
    addLinkItem,
    updateLinkItemUrl,
    deleteLinkItem,
    toggleFav,
    toggleIsSelected,
    toggleFocus
} = navBarSlice.actions

export default navBarSlice.reducer
