import React from 'react'

import '../../features/linkItemSearch/LinkItemSearchStyle.css'

import { useDispatch, useSelector } from 'react-redux';
import { selectSearchText, updateSearchText, clearSearchText } from '../linkItemSearch/linkItemSearchSlice'
import { forceStateChange } from '../navBarList/navBarSlice'

const LinkItemSearch = () => {
  const dispatch = useDispatch()  
  const text = useSelector(selectSearchText)

  const onClickClear = () => {
    dispatch(clearSearchText())
  }

  const onClickSearch = (e) => {
    //dispatch(clearSearchText)
  }

  const onChangeText = ({ target }) => {
    dispatch(updateSearchText(target.value))
    // force re-render on navLinkList by changing its state and making the list items pass through the filter
    dispatch(forceStateChange()) 
    //dispatch((target.value))
  }

  return (
    <div className='linkItemSearch'>
      <input onChange={onChangeText} className='linkItemSearchInput' value={text} placeholder='Search' type='text'></input>
      {/* <button onClick={onClickClear} className='linkItemSearchClearhBtn'>clear</button> */}
      {/* <button onClick={onClickSearch} className='linkItemSearchSearchBtn'>Search</button>      */}
      
      <label className='linkItemSearchLable'>favs only</label>
      <input className='linkItemSearchFavToggle' type='checkbox' placeholder='favs only'></input>  
    </div>
  )
}

export default LinkItemSearch
