import React from 'react'

import '../../features/linkItemSearch/LinkItemSearchStyle.css'

import { useDispatch, useSelector } from 'react-redux';
import { selectText, updateText, clearText } from '../linkItemSearch/linkItemSearchSlice'

const LinkItemSearch = () => {
  const dispatch = useDispatch()

  const text = useSelector(selectText)

  const onClickClear = () => {
    dispatch(clearText)
  }

  const onClickSearch = (e) => {
    dispatch(clearText)
  }  

  const onChangeText = ({ target }) => {
    if(target.value)
      dispatch(updateText(target.value))
  }

  return (
    <div className='linkItemSearch'>
      <input onChange={onChangeText} className='linkItemSearchInput' placeholder='Search links' type='text' value={text}></input>
      <button onClick={onClickClear} className='linkItemSearchClearhBtn'>clear</button>
      <button onClick={onClickSearch} className='linkItemSearchSearchBtn'>Search</button>     
      
      <label>favs only</label>       
      <input className='linkItemSearchFavToggle' type='checkbox' placeholder='favs only'></input>  
    </div>    
  )
}

export default LinkItemSearch
