import React from 'react'
import { useDispatch, useSelector } from 'react-redux'

import {
  // selectFocusedUrlText,
  // selectFocusedNote,

  updateLinkItemUrl,  
  toggleFocusUrl,
  toggleFocusNote,
  deleteLinkItem,
  toggleFav,
  updateLinkItemNote  
} from '../navBarList/navBarSlice'

import '../../features/linkItem/linkItemStyle.css'

const LinkItem = (props) => { // linkItemKey
  const dispatch = useDispatch()

  // let url = useSelector(selectFocusedUrlText)
  // let note = useSelector(selectFocusedNote)  
  // if(url === undefined){
  //   url = props.url    
  //   note = props.note 
  // }
  
  // let lastUrl = url
  // let lastNote = note
  
  

  const onChangeUrl = ({ target }) => {
    dispatch(updateLinkItemUrl(target.value))
  }

  //https://www.youtube.com/watch?v=4RgQ-JDiOic
  const onClickGoto = () => {
    window.open(props.url)
    //console.log(url)
  }

  const onClickToggleFav = () => {
    dispatch(toggleFav(props.linkItemKey))
  }

  const onClickBin = () => {
    dispatch(deleteLinkItem(props.linkItemKey))
  }

  const onUrlFocus = () => {
    dispatch(toggleFocusUrl(props.linkItemKey))
  }

  const onBlur = () => {
    dispatch(toggleFocusUrl(props.linkItemKey))
  }

  const onUrlFocusNote = () => {
    dispatch(toggleFocusNote(props.linkItemKey))
  }

  const onBlurNote = () => {
    dispatch(toggleFocusNote(props.linkItemKey))
  }

  const onChnageNote = ({ target }) => {
    dispatch(updateLinkItemNote(target.value))
  }

  return (
    <div className='linkItem'>
        {/* <input onFocus={onUrlFocus} value={props.url} onBlur={onBlur} onChange={onChangeUrl} type='text' className='linkItemLabel' placeholder='url'></input>         */}
        <input readOnly value={props.url} type='text' className='linkItemLabel' placeholder='url'></input>        
        <input onFocus={onUrlFocusNote} value={props.note} onBlur={onBlurNote} onChange={onChnageNote} type='text' className='linkItemName' placeholder='note'></input>        
        
        <button onClick={onClickGoto} className='linkItemGotoLinkBtn'>goto</button> {/* when clicked another tab of the link opens */}
        <button onClick={onClickToggleFav} className='linkItemFavBtn'>fav</button>
        <button onClick={onClickBin} className='linkItemBinBtn'>bin</button>        
    </div>
  )
}

export default LinkItem
