import React from 'react'

import { useSelector, useDispatch } from 'react-redux';
import { selectText, updateText, clearText } from '../linkItemAdd/linkItemAddSlice'

import '../../features/linkItemAdd/linkItemAddStyle.css'

const LinkItemAdd = () => {
  const dispatch = useDispatch()
  const text = useSelector(selectText)  

  const onChangeText = (e) => {
    dispatch(updateText(e.target.value))
    //console.log('onChangeText: '+text)
  }

  const onClickLinkItemAdd = (e) => {
    console.log('text-1: '+text)
    //dispatch() add to nav bar list
    dispatch(clearText())
    console.log('text-2: '+text)
  }
  
  return (
    <div className='linkItemAdd'>
      <input onChange={onChangeText} className='linkItemAddInput' placeholder='Add new link'></input>
      <button onClick={onClickLinkItemAdd} className='linkItemAddBtn'>Add</button> 
    </div>
  )
}

export default LinkItemAdd
