import { createSlice } from '@reduxjs/toolkit'

import { newKey } from '../../utils'

// =============== Slice Object ===============
export const navBarSlice = createSlice({
    name:'navBarList',
    initialState:[],
    reducers:{
        addNavItem:(state, action) => {         
            state.push({
                navItemKey:newKey(),
                title:action.payload,
                isSelected:false,
                linkItemList:[]    
            })
        },        
        // navItemKey
        // title
        // isSelected
        // linkItemList

        deleteNavItem:(state, action) => {
            if(state.length === 1)
                return []

            // index of navItem to delete
            let removeIndex
            for(let i=0; i<state.length; ++i){
                if(action.payload === state[i].navItemKey){
                    removeIndex = i
                    return [
                        ...state.slice(0, removeIndex),
                        ...state.slice(removeIndex+1, state.length)
                    ]   
                }
            }                      
        },

        toggleIsSelected:(state, action) => {
            // first de-select the current selected nav item
            for(let i=0; i<state.length; ++i){
                if(state[i].isSelected)
                    state[i].isSelected = false
            }
            // second, find the nav item which needs to be selected
            for(let i=0; i<state.length; ++i)
                if(state[i].navItemKey === action.payload)
                    state[i].isSelected = true

            return state
        },

        addLinkItem:(state, action) => {            
            for(let i=0; i<state.length; ++i){
                if(state[i].isSelected){                    
                    state[i].linkItemList.push({
                        linkItemKey:newKey(),
                        url:action.payload,
                        isFav:false
                    })                    
                }                
            }            
                // navItemKey
                // title (nav bar display text)  
                // sub list of urls
                    // linkItemKey
                    // url
                    // isFav

        },
        updateLinkItemUrl:(state, action) => {

        },
        deleteLinkItem:(state, action) => {
            //return state.
        },

        toggleFav:(state, action) => {

        }
    }
})

// =============== Selectors ===============
export const selectNavBarList = state => state.navBarList

export const selectIsSelectedLinkItemList = (state) => {
    if(state){
        console.log('state is true - state: ')
        console.log(...state)
    }

    for(let i=0; i<state.length; ++i)
        if(state[i].isSelected)
            return state[i].linkItemList
    //return []
}

// =============== Exports ===============
export const {
    addNavItem,
    deleteNavItem,
    addLinkItem,
    updateLinkItemUrl,
    deleteLinkItem,
    toggleFav,
    toggleIsSelected
} = navBarSlice.actions

export default navBarSlice.reducer
