import React from 'react'
import { useSelector, useDispatch } from 'react-redux'

import { selectText, updateText, clearText } from '../linkItemAdd/linkItemAddSlice'
import { addLinkItem } from '../navBarList/navBarSlice'

import '../../features/linkItemAdd/linkItemAddStyle.css'

const LinkItemAdd = () => {
  const dispatch = useDispatch()
  const text = useSelector(selectText) // url
  const textName = useSelector()

  const onChangeText = ({ target }) => {
    dispatch(updateText({url: target.value, textName:textName}))
    //console.log('onChangeText: '+text)
  }

  const onClickAddUrl = () => {
    if(text){
      dispatch(addLinkItem(text))
      dispatch(clearText())
    }    
  }

  const onClickAddName = () => {
    if(text){
      dispatch(addLinkItem(text))
      dispatch(clearText())
    }    
  }

  const onChangeName = ({ target }) => {
    dispatch(...(target.value))
    //console.log('onChangeText: '+text)
  }
  
  const onKeyDownUrl = ({ key }) => {    
    if(key === 'Enter')
      onClickAddUrl()
  }

  const onEnter = ({ key }) => {    
    if(key === 'Enter')
      onClickAddName()
  }

  return (
    <div className='linkItemAdd'>
      <input onKeyDown={onEnter} onChange={onChangeText} value={text} className='linkItemAddInput' placeholder='Add new link'></input>
      <input onKeyDown={onEnter} onChange={onChangeName} value={textName} type='text' className='linkItemAddName' placeholder='Add a note'></input>
    
      <button onClick={onClickAddUrl} className='linkItemAddBtn'>Add</button> 
    </div>
  )
}

export default LinkItemAdd
