import React from 'react'
import { useSelector, useDispatch } from 'react-redux';

import { selectNavBarAddText, updateText, clearText } from '../navBarAdd/navBarAddSlice'

import '../navBarAdd/navBarAddStyle.css'

const NavBarAdd = () => {
  const text = useSelector(selectNavBarAddText)
  const dispatch = useDispatch()

  const onClickAdd = () => {
    //dispatch()
    dispatch(clearText)
  }

  const onChangeText = (e) => {
    dispatch(updateText(e.targat.value))
  }

  return (
    <div className='navBarAdd'>
      <input onChange={onChangeText} className='navBarAddInput' placeholder='Add new list' value={text}></input>
      <button onClick={onClickAdd} className='navBarAddBtn'>Add</button> 
    </div>
    
  )
}

export default NavBarAdd
