import React from 'react'
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';

import { updateLinkItemUrl } from '../navBarList/navBarSlice'

import '../../features/linkItem/linkItemStyle.css'

const LinkItem = (props) => { // linkItemKey
  //const = useSelector(selectIsSelected)
  const dispatch = useDispatch()

  const onChangeUrl = () => {
    dispatch(updateLinkItemUrl())
  }

  return (
    <div className='linkItem'>
        <input onChange={onChangeUrl} type='text' className='linkItemLabel' value={props.url}></input>        
        <button className='linkItemGotoLinkBtn'>goto</button> {/* when clicked another tab of the link opens */}
        <button className='linkItemFavBtn'>fav</button>
        <button className='linkItemBinBtn'>bin</button>        
    </div>
  )
}

export default LinkItem
